    // Инцелизация toggleButtonWeek menu для нидели
    private fun init_toggleButtonWeek() {
        mainBinding.toggleButtonWeek.addOnButtonCheckedListener(
            MaterialButtonToggleGroup.OnButtonCheckedListener { group, checkedId, isChecked ->
                if (isChecked) {
                    weekItem = when (checkedId) {
                        mainBinding.buttonDenominator.id -> 0
                        mainBinding.buttonToday.id -> 0
                        mainBinding.buttonNumerator.id -> 1
                        else -> 1
                    }

                    init_day_list()
                }
            })
    }


    // Инцелизация Spiner menu для групп
    private fun init_listGroup(){
        db.getDao().GetItemNumGroup().asLiveData().observe(this){
            init_AutoCompleteTextGroup(it.sortedBy{it_num -> it_num.toInt() })
        }
    }

    private fun init_AutoCompleteTextGroup(list: List<String>){
        autoComplecteGroup = findViewById(R.id.auto_complecte_txt)

        adapterGroup = ArrayAdapter(this, R.layout.list_item, list)
        autoComplecteGroup.setAdapter(adapterGroup)

        autoComplecteGroup.setOnItemClickListener { parent, view, position, id ->
            groupItem = parent.getItemAtPosition(position).toString()
            init_day_list()
        }
    }
    private fun day_list(Lesson: List<Lesson_info>){
        recyclerView = mainBinding.RViewDay
        adapter = SH_day_adapter()
        recyclerView.adapter = adapter

        var day_week = listOf<String>("Пн", "Вт","Ср", "Чт","Пт", "Сб")

        DayList = arrayListOf()
        day_week.forEach{it_day ->
            Log.d("MYLOGMain", "${it_day} \n")
            var Les: ArrayList<Lesson_info> = arrayListOf()
            Lesson.forEach{ it_Lesson -> if (it_Lesson.day == it_day){ Les.add(it_Lesson) } }
            Les.sortBy { it.NumLesson }

            DayList.add(day_info(it_day.toString(), Les))
        }

        Log.d("MYLOGMain", "${DayList} \n")


        recyclerView.scheduleLayoutAnimation()

        val numberOfColumns = 1
        recyclerView.layoutManager = GridLayoutManager(this, numberOfColumns)
        adapter.setScheduleList(DayList)
    }


    private fun init_day_list() {
        db.getDao().GetItemSH(groupItem, weekItem).asLiveData().observe(this){ day_list(it) }
    }



